import React from 'react';

export default function About() {
  return (
    <div className="about-container">
      <h2> אודות האתר הזה </h2>
      <p>🌍 אפליקציית גלריית הדגלים הזו נוצרה כדי להציג את הדגלים היפים והמגוונים של העולם. לכל דגל יש סיפור משלו והוא מייצג את תרבות והיסטוריה של מדינתו.🌍 </p>
      <p>🌍 פותח על ידי שרגא נתן, הפרויקט הזה שואף לחבר בין אנשים למדינות ברחבי העולם דרך הדגלים שלהם, ולהציע חלון אל זהותם של כל אומה.🌍 </p>
      
      <div className="about-me">
        <h3>אודותי</h3>
        <p>🌍 אני שרגא, מפתח אינטרנט תשוקתי שאוהב לחקור תרבויות והיסטוריות שונות. הפרויקט הזה משלב את תשוקתי לפיתוח ואת סקרנותי על העולם. 🌍</p>
        <p>לפרויקטים נוספים ומידע, אתם מוזמנים לבקר ב-<a href="https://github.com/yourgithub">GitHub</a> שלי או להתחבר איתי ב-<a href="https://linkedin.com/in/yourlinkedin">LinkedIn</a>.</p>
      </div>
    </div>
  );
}
